contracts/DragonCityNFT.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC721/ERC721.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";

/**
 * @title DragonCityNFT
 * @dev Full Dragon City implementation:
 * - Training & Evolution system
 * - Stat-based progression
 * - User-created dragons via evolution
 * - Marketplace with installment payments
 * - Tokenomics via training fees
 */
contract DragonCityNFT is ERC721, Ownable, ReentrancyGuard {
    
    // ============ ENUMS ============
    
    enum Element { FIRE, WATER, EARTH, AIR, DARK, LIGHT, NATURE, METAL, ICE, ELECTRIC }
    enum Rarity { COMMON, RARE, EPIC, LEGENDARY, MYTHIC }
    enum DragonForm { BASIC, EVOLVED1, EVOLVED2, ULTIMATE }
    
    // ============ STRUCTS ============
    
    struct Dragon {
        uint256 id;
        string name; // User can name their dragon
        Element primary;
        Element secondary; // Dual elements like real Dragon City
        Rarity rarity;
        DragonForm form;
        
        // Stats (increase with training)
        uint256 attack;
        uint256 defense;
        uint256 health;
        uint256 speed;
        
        // Progression
        uint256 level;
        uint256 experience;
        uint256 trainingCount;
        
        // Battle records
        uint256 wins;
        uint256 losses;
        
        // Metadata
        uint256 birthTime;
        uint256 lastTrainingTime;
        uint256 parentId1;
        uint256 parentId2;
        bool isEvolved;
    }
    
    struct TrainingSession {
        uint256 dragonId;
        uint256 startTime;
        uint256 duration; // Training takes time
        uint256 feePaid;
        string statToTrain; // "attack", "defense", "health", "speed"
        bool completed;
    }
    
    struct MarketListing {
        uint256 dragonId;
        address seller;
        uint256 price;
        bool acceptsInstallments;
        bool active;
        uint256 listedAt;
    }
    
    struct InstallmentPlan {
        uint256 listingId;
        address buyer;
        uint256 totalPrice;
        uint256 paidAmount;
        uint256 installmentsPaid; // 0, 1, 2, 3
        uint256 lastPaymentTime;
        bool completed;
    }
    
    // ============ STATE VARIABLES ============
    
    uint256 private _tokenIdCounter = 1;
    uint256 private _listingIdCounter = 1;
    
    // Training costs (in wei or token)
    uint256 public baseTrainingFee = 0.001 ether;
    uint256 public evolutionFee = 0.01 ether;
    
    // Training duration
    uint256 public constant TRAINING_DURATION = 1 hours;
    uint256 public constant EVOLUTION_DURATION = 24 hours;
    
    // Marketplace
    uint256 public constant MARKETPLACE_FEE = 250; // 2.5%
    uint256 public constant INSTALLMENT_COUNT = 3;
    uint256 public constant PAYMENT_INTERVAL = 24 hours;
    
    // Token for training fees (optional, can use ETH)
    IERC20 public gameToken;
    bool public useTokenForFees;
    
    // Mappings
    mapping(uint256 => Dragon) public dragons;
    mapping(address => uint256[]) public userDragons;
    mapping(uint256 => TrainingSession) public activeSessions;
    mapping(uint256 => MarketListing) public listings;
    mapping(uint256 => InstallmentPlan) public installmentPlans;
    mapping(address => uint256[]) public userListings;
    
    uint256[] public activeListingIds;
    
    // Evolution requirements
    mapping(Rarity => uint256) public evolutionLevelRequired;
    
    // ============ EVENTS ============
    
    event DragonBorn(uint256 indexed dragonId, address indexed owner, Element primary, Element secondary);
    event DragonNamed(uint256 indexed dragonId, string name);
    event TrainingStarted(uint256 indexed dragonId, string stat, uint256 duration);
    event TrainingCompleted(uint256 indexed dragonId, string stat, uint256 improvement);
    event DragonEvolved(uint256 indexed dragonId, DragonForm newForm);
    event DragonListed(uint256 indexed listingId, uint256 indexed dragonId, uint256 price);
    event DragonSold(uint256 indexed dragonId, address from, address to, uint256 price);
    event InstallmentPayment(uint256 indexed listingId, address buyer, uint256 amount);
    
    // ============ CONSTRUCTOR ============
    
    constructor() ERC721("Dragon City", "DRAGON") Ownable(msg.sender) {
        // Set evolution requirements
        evolutionLevelRequired[Rarity.COMMON] = 10;
        evolutionLevelRequired[Rarity.RARE] = 15;
        evolutionLevelRequired[Rarity.EPIC] = 20;
        evolutionLevelRequired[Rarity.LEGENDARY] = 30;
        evolutionLevelRequired[Rarity.MYTHIC] = 50;
    }
    
    // ============ DRAGON CREATION ============
    
    /**
     * @dev Create initial dragon (from egg hatching)
     */
    function createDragon(
        Element primary,
        Element secondary,
        Rarity rarity
    ) external payable returns (uint256) {
        require(msg.value >= 0.01 ether, "Creation fee required");
        
        uint256 dragonId = _tokenIdCounter++;
        
        // Base stats depend on rarity
        uint256 baseStats = _getBaseStatsForRarity(rarity);
        
        dragons[dragonId] = Dragon({
            id: dragonId,
            name: string(abi.encodePacked("Dragon #", _toString(dragonId))),
            primary: primary,
            secondary: secondary,
            rarity: rarity,
            form: DragonForm.BASIC,
            attack: baseStats,
            defense: baseStats,
            health: baseStats * 2,
            speed: baseStats,
            level: 1,
            experience: 0,
            trainingCount: 0,
            wins: 0,
            losses: 0,
            birthTime: block.timestamp,
            lastTrainingTime: 0,
            parentId1: 0,
            parentId2: 0,
            isEvolved: false
        });
        
        userDragons[msg.sender].push(dragonId);
        _safeMint(msg.sender, dragonId);
        
        emit DragonBorn(dragonId, msg.sender, primary, secondary);
        
        return dragonId;
    }
    
    /**
     * @dev Breed two dragons to create new one
     */
    function breedDragons(uint256 parent1Id, uint256 parent2Id) external payable nonReentrant returns (uint256) {
        require(ownerOf(parent1Id) == msg.sender, "Not owner of parent 1");
        require(ownerOf(parent2Id) == msg.sender, "Not owner of parent 2");
        require(dragons[parent1Id].level >= 4, "Parent 1 level too low");
        require(dragons[parent2Id].level >= 4, "Parent 2 level too low");
        require(msg.value >= 0.05 ether, "Breeding fee required");
        
        Dragon memory p1 = dragons[parent1Id];
        Dragon memory p2 = dragons[parent2Id];
        
        // Child inherits elements from parents
        Element childPrimary = p1.primary;
        Element childSecondary = p2.primary;
        
        // Higher rarity parent determines child rarity
        Rarity childRarity = p1.rarity > p2.rarity ? p1.rarity : p2.rarity;
        
        uint256 dragonId = _tokenIdCounter++;
        uint256 baseStats = _getBaseStatsForRarity(childRarity);
        
        // Inherit some parent stats
        uint256 inheritedAttack = (p1.attack + p2.attack) / 2;
        uint256 inheritedDefense = (p1.defense + p2.defense) / 2;
        
        dragons[dragonId] = Dragon({
            id: dragonId,
            name: string(abi.encodePacked("Dragon #", _toString(dragonId))),
            primary: childPrimary,
            secondary: childSecondary,
            rarity: childRarity,
            form: DragonForm.BASIC,
            attack: (baseStats + inheritedAttack) / 2,
            defense: (baseStats + inheritedDefense) / 2,
            health: baseStats * 2,
            speed: baseStats,
            level: 1,
            experience: 0,
            trainingCount: 0,
            wins: 0,
            losses: 0,
            birthTime: block.timestamp,
            lastTrainingTime: 0,
            parentId1: parent1Id,
            parentId2: parent2Id,
            isEvolved: false
        });
        
        userDragons[msg.sender].push(dragonId);
        _safeMint(msg.sender, dragonId);
        
        emit DragonBorn(dragonId, msg.sender, childPrimary, childSecondary);
        
        return dragonId;
    }
    
    // ============ NAMING ============
    
    function nameDragon(uint256 dragonId, string memory newName) external {
        require(ownerOf(dragonId) == msg.sender, "Not owner");
        require(bytes(newName).length > 0 && bytes(newName).length <= 32, "Invalid name length");
        
        dragons[dragonId].name = newName;
        emit DragonNamed(dragonId, newName);
    }
    
    // ============ TRAINING SYSTEM ============
    
    /**
     * @dev Start training session (tokenomics - fees required)
     */
    function startTraining(uint256 dragonId, string memory stat) external payable nonReentrant {
        require(ownerOf(dragonId) == msg.sender, "Not owner");
        require(activeSessions[dragonId].completed || activeSessions[dragonId].startTime == 0, "Training in progress");
        
        uint256 fee = baseTrainingFee * (dragons[dragonId].level / 10 + 1);
        require(msg.value >= fee, "Insufficient training fee");
        
        activeSessions[dragonId] = TrainingSession({
            dragonId: dragonId,
            startTime: block.timestamp,
            duration: TRAINING_DURATION,
            feePaid: msg.value,
            statToTrain: stat,
            completed: false
        });
        
        emit TrainingStarted(dragonId, stat, TRAINING_DURATION);
    }
    
    /**
     * @dev Complete training and apply stat boost
     */
    function completeTraining(uint256 dragonId) external {
        require(ownerOf(dragonId) == msg.sender, "Not owner");
        
        TrainingSession storage session = activeSessions[dragonId];
        require(session.startTime > 0, "No training session");
        require(!session.completed, "Already completed");
        require(block.timestamp >= session.startTime + session.duration, "Training not finished");
        
        Dragon storage dragon = dragons[dragonId];
        
        // Calculate improvement based on fee paid and level
        uint256 improvement = (session.feePaid / baseTrainingFee) * 5;
        
        // Apply to correct stat
        if (keccak256(bytes(session.statToTrain)) == keccak256(bytes("attack"))) {
            dragon.attack += improvement;
        } else if (keccak256(bytes(session.statToTrain)) == keccak256(bytes("defense"))) {
            dragon.defense += improvement;
        } else if (keccak256(bytes(session.statToTrain)) == keccak256(bytes("health"))) {
            dragon.health += improvement;
        } else if (keccak256(bytes(session.statToTrain)) == keccak256(bytes("speed"))) {
            dragon.speed += improvement;
        }
        
        dragon.trainingCount++;
        dragon.lastTrainingTime = block.timestamp;
        session.completed = true;
        
        // Gain XP from training
        dragon.experience += 50;
        _checkLevelUp(dragonId);
        
        emit TrainingCompleted(dragonId, session.statToTrain, improvement);
    }
    
    // ============ EVOLUTION SYSTEM ============
    
    /**
     * @dev Evolve dragon to next form (user creates new dragon variant)
     */
    function evolveDragon(uint256 dragonId) external payable nonReentrant {
        require(ownerOf(dragonId) == msg.sender, "Not owner");
        
        Dragon storage dragon = dragons[dragonId];
        require(dragon.form != DragonForm.ULTIMATE, "Already ultimate form");
        require(dragon.level >= evolutionLevelRequired[dragon.rarity], "Level too low");
        require(msg.value >= evolutionFee, "Insufficient evolution fee");
        
        // Upgrade form
        if (dragon.form == DragonForm.BASIC) dragon.form = DragonForm.EVOLVED1;
        else if (dragon.form == DragonForm.EVOLVED1) dragon.form = DragonForm.EVOLVED2;
        else if (dragon.form == DragonForm.EVOLVED2) dragon.form = DragonForm.ULTIMATE;
        
        // Stat boost on evolution
        uint256 boost = 20 + (uint256(dragon.rarity) * 10);
        dragon.attack += boost;
        dragon.defense += boost;
        dragon.health += boost * 2;
        dragon.speed += boost;
        dragon.isEvolved = true;
        
        emit DragonEvolved(dragonId, dragon.form);
    }
    
    // ============ MARKETPLACE WITH INSTALLMENTS ============
    
    /**
     * @dev List dragon for sale
     */
    function listDragon(uint256 dragonId, uint256 price, bool acceptInstallments) external {
        require(ownerOf(dragonId) == msg.sender, "Not owner");
        require(price > 0, "Invalid price");
        
        uint256 listingId = _listingIdCounter++;
        
        listings[listingId] = MarketListing({
            dragonId: dragonId,
            seller: msg.sender,
            price: price,
            acceptsInstallments: acceptInstallments,
            active: true,
            listedAt: block.timestamp
        });
        
        activeListingIds.push(listingId);
        userListings[msg.sender].push(listingId);
        
        // Transfer dragon to contract for escrow
        _transfer(msg.sender, address(this), dragonId);
        
        emit DragonListed(listingId, dragonId, price);
    }
    
    /**
     * @dev Buy dragon with full payment
     */
    function buyDragon(uint256 listingId) external payable nonReentrant {
        MarketListing storage listing = listings[listingId];
        require(listing.active, "Not active");
        require(msg.value >= listing.price, "Insufficient payment");
        
        uint256 fee = (listing.price * MARKETPLACE_FEE) / 10000;
        uint256 sellerAmount = listing.price - fee;
        
        address seller = listing.seller;
        uint256 dragonId = listing.dragonId;
        
        listing.active = false;
        
        // Transfer dragon to buyer
        _transfer(address(this), msg.sender, dragonId);
        
        // Pay seller
        payable(seller).transfer(sellerAmount);
        
        emit DragonSold(dragonId, seller, msg.sender, listing.price);
    }
    
    /**
     * @dev Start installment purchase (3 payments)
     */
    function startInstallmentPurchase(uint256 listingId) external payable nonReentrant {
        MarketListing storage listing = listings[listingId];
        require(listing.active, "Not active");
        require(listing.acceptsInstallments, "Installments not accepted");
        
        uint256 firstPayment = listing.price / INSTALLMENT_COUNT;
        require(msg.value >= firstPayment, "Insufficient first payment");
        
        installmentPlans[listingId] = InstallmentPlan({
            listingId: listingId,
            buyer: msg.sender,
            totalPrice: listing.price,
            paidAmount: msg.value,
            installmentsPaid: 1,
            lastPaymentTime: block.timestamp,
            completed: false
        });
        
        emit InstallmentPayment(listingId, msg.sender, msg.value);
    }
    
    /**
     * @dev Continue installment payments
     */
    function payInstallment(uint256 listingId) external payable nonReentrant {
        InstallmentPlan storage plan = installmentPlans[listingId];
        require(plan.buyer == msg.sender, "Not buyer");
        require(!plan.completed, "Already completed");
        require(block.timestamp >= plan.lastPaymentTime + PAYMENT_INTERVAL, "Too soon");
        
        uint256 paymentAmount = plan.totalPrice / INSTALLMENT_COUNT;
        require(msg.value >= paymentAmount, "Insufficient payment");
        
        plan.paidAmount += msg.value;
        plan.installmentsPaid++;
        plan.lastPaymentTime = block.timestamp;
        
        emit InstallmentPayment(listingId, msg.sender, msg.value);
        
        // Complete if all paid
        if (plan.installmentsPaid >= INSTALLMENT_COUNT) {
            plan.completed = true;
            _completeInstallmentSale(listingId);
        }
    }
    
    function _completeInstallmentSale(uint256 listingId) private {
        MarketListing storage listing = listings[listingId];
        InstallmentPlan storage plan = installmentPlans[listingId];
        
        uint256 fee = (listing.price * MARKETPLACE_FEE) / 10000;
        uint256 sellerAmount = listing.price - fee;
        
        address seller = listing.seller;
        uint256 dragonId = listing.dragonId;
        
        listing.active = false;
        
        // Transfer dragon to buyer
        _transfer(address(this), plan.buyer, dragonId);
        
        // Pay seller
        payable(seller).transfer(sellerAmount);
        
        emit DragonSold(dragonId, seller, plan.buyer, listing.price);
    }
    
    /**
     * @dev Cancel listing
     */
    function cancelListing(uint256 listingId) external {
        MarketListing storage listing = listings[listingId];
        require(listing.seller == msg.sender, "Not seller");
        require(listing.active, "Not active");
        
        listing.active = false;
        
        // Return dragon to seller
        _transfer(address(this), msg.sender, listing.dragonId);
    }
    
    // ============ INTERNAL HELPERS ============
    
    function _getBaseStatsForRarity(Rarity rarity) private pure returns (uint256) {
        if (rarity == Rarity.COMMON) return 50;
        if (rarity == Rarity.RARE) return 75;
        if (rarity == Rarity.EPIC) return 100;
        if (rarity == Rarity.LEGENDARY) return 150;
        return 200; // MYTHIC
    }
    
    function _checkLevelUp(uint256 dragonId) private {
        Dragon storage dragon = dragons[dragonId];
        uint256 requiredXP = dragon.level * 100;
        
        while (dragon.experience >= requiredXP) {
            dragon.level++;
            dragon.attack += 3;
            dragon.defense += 3;
            dragon.health += 5;
            dragon.speed += 2;
            requiredXP = dragon.level * 100;
        }
    }
    
    function _toString(uint256 value) private pure returns (string memory) {
        if (value == 0) return "0";
        uint256 temp = value;
        uint256 digits;
        while (temp != 0) {
            digits++;
            temp /= 10;
        }
        bytes memory buffer = new bytes(digits);
        while (value != 0) {
            digits -= 1;
            buffer[digits] = bytes1(uint8(48 + uint256(value % 10)));
            value /= 10;
        }
        return string(buffer);
    }
    
    // ============ VIEW FUNCTIONS ============
    
    function getDragon(uint256 dragonId) external view returns (Dragon memory) {
        return dragons[dragonId];
    }
    
    function getUserDragons(address user) external view returns (uint256[] memory) {
        return userDragons[user];
    }
    
    function getActiveListings() external view returns (uint256[] memory) {
        return activeListingIds;
    }
    
    function getBattlePower(uint256 dragonId) external view returns (uint256) {
        Dragon memory d = dragons[dragonId];
        return (d.attack + d.defense + d.speed) * d.level;
    }
    
    // ============ ADMIN ============
    
    function setGameToken(address _token) external onlyOwner {
        gameToken = IERC20(_token);
    }
    
    function setTrainingFee(uint256 _fee) external onlyOwner {
        baseTrainingFee = _fee;
    }
    
    function withdraw() external onlyOwner {
        payable(owner()).transfer(address(this).balance);
    }
}

contracts/DragonNFTWithRarity.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC721/ERC721.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/Strings.sol";

interface IEggNFT {
    enum Rarity { COMMON, RARE, UNIQUE, LEGENDARY }
    
    function eggs(uint256 tokenId) external view returns (
        Rarity rarity,
        uint256 batchId,
        bool isHatched,
        uint256 mintedAt
    );
    
    function ownerOf(uint256 tokenId) external view returns (address);
    function transferFrom(address from, address to, uint256 tokenId) external;
}

/**
 * @title DragonNFTWithRarity
 * @dev Dragons inherit rarity from eggs, affecting stats and visuals
 * Supports OpenSea/Magic Eden metadata
 */
contract DragonNFTWithRarity is ERC721, Ownable {
    using Strings for uint256;

    enum Element { FIRE, WATER, EARTH, AIR, DARK, LIGHT }
    enum PowerType { PHYSICAL, MAGICAL, HYBRID }
    enum Rarity { COMMON, RARE, UNIQUE, LEGENDARY }

    struct Dragon {
        uint256 id;
        Element element;
        PowerType powerType;
        Rarity rarity; // Inherited from egg
        uint256 attack;
        uint256 defense;
        uint256 speed;
        uint256 level;
        uint256 experience;
        uint256 wins;
        uint256 losses;
        uint256 birthTime;
        uint256 parentEggId; // Track which egg it came from
    }

    uint256 private _tokenIdCounter;
    string private _baseTokenURI;
    
    IEggNFT public eggNFT;
    address public leaderboardContract;

    // Rarity stat multipliers (basis points, 10000 = 1x)
    mapping(Rarity => uint256) public rarityMultipliers;

    // Dragon data
    mapping(uint256 => Dragon) public dragons;
    mapping(address => uint256[]) public userDragons;
    
    // Events
    event DragonHatched(uint256 indexed dragonId, address indexed owner, Element element, Rarity rarity);
    event DragonLevelUp(uint256 indexed dragonId, uint256 newLevel);
    event BattleResult(uint256 indexed winnerId, uint256 indexed loserId);

    constructor(address _eggNFT) ERC721("Dragon City Dragon", "DRAGON") Ownable(msg.sender) {
        _tokenIdCounter = 1;
        eggNFT = IEggNFT(_eggNFT);
        
        // Set rarity multipliers (basis points)
        rarityMultipliers[Rarity.COMMON] = 10000;    // 1.0x
        rarityMultipliers[Rarity.RARE] = 12500;      // 1.25x
        rarityMultipliers[Rarity.UNIQUE] = 15000;    // 1.5x
        rarityMultipliers[Rarity.LEGENDARY] = 20000; // 2.0x
    }

    // ============ ADMIN FUNCTIONS ============

    function setEggNFT(address _eggNFT) external onlyOwner {
        eggNFT = IEggNFT(_eggNFT);
    }

    function setLeaderboard(address _leaderboard) external onlyOwner {
        leaderboardContract = _leaderboard;
    }

    function setBaseURI(string memory baseURI) external onlyOwner {
        _baseTokenURI = baseURI;
    }

    // ============ HATCHING ============

    /**
     * @dev Hatch an egg into a dragon (inherits rarity)
     */
    function hatchEgg(uint256 eggId) external returns (uint256) {
        require(eggNFT.ownerOf(eggId) == msg.sender, "Not egg owner");
        
        // Get egg rarity
        (IEggNFT.Rarity eggRarity, , bool isHatched, ) = eggNFT.eggs(eggId);
        require(!isHatched, "Egg already hatched");
        
        // Generate random element and power type
        Element element = Element(uint256(keccak256(abi.encodePacked(block.timestamp, eggId, msg.sender, "element"))) % 6);
        PowerType powerType = PowerType(uint256(keccak256(abi.encodePacked(block.timestamp, eggId, msg.sender, "power"))) % 3);
        
        // Convert egg rarity to dragon rarity
        Rarity dragonRarity = Rarity(uint8(eggRarity));
        
        // Calculate base stats with rarity multiplier
        uint256 baseAttack = _generateBaseStat(eggId, "attack");
        uint256 baseDefense = _generateBaseStat(eggId, "defense");
        uint256 baseSpeed = _generateBaseStat(eggId, "speed");
        
        uint256 multiplier = rarityMultipliers[dragonRarity];
        
        uint256 dragonId = _tokenIdCounter++;
        
        dragons[dragonId] = Dragon({
            id: dragonId,
            element: element,
            powerType: powerType,
            rarity: dragonRarity,
            attack: (baseAttack * multiplier) / 10000,
            defense: (baseDefense * multiplier) / 10000,
            speed: (baseSpeed * multiplier) / 10000,
            level: 1,
            experience: 0,
            wins: 0,
            losses: 0,
            birthTime: block.timestamp,
            parentEggId: eggId
        });
        
        userDragons[msg.sender].push(dragonId);
        _safeMint(msg.sender, dragonId);
        
        emit DragonHatched(dragonId, msg.sender, element, dragonRarity);
        
        return dragonId;
    }

    function _generateBaseStat(uint256 seed, string memory statName) private view returns (uint256) {
        uint256 random = uint256(keccak256(abi.encodePacked(block.timestamp, seed, statName)));
        return 50 + (random % 51); // 50-100 base
    }

    // ============ BATTLE SYSTEM ============

    function recordBattleResult(uint256 winnerId, uint256 loserId, uint256 expGained) external {
        require(msg.sender == leaderboardContract || msg.sender == owner(), "Not authorized");
        
        dragons[winnerId].wins++;
        dragons[winnerId].experience += expGained;
        dragons[loserId].losses++;
        
        _checkLevelUp(winnerId);
        
        emit BattleResult(winnerId, loserId);
    }

    function _checkLevelUp(uint256 dragonId) private {
        Dragon storage dragon = dragons[dragonId];
        uint256 requiredExp = dragon.level * 100;
        
        while (dragon.experience >= requiredExp) {
            dragon.level++;
            dragon.attack += 5;
            dragon.defense += 5;
            dragon.speed += 3;
            
            emit DragonLevelUp(dragonId, dragon.level);
            requiredExp = dragon.level * 100;
        }
    }

    // ============ BREEDING ============

    function breedDragons(uint256 parent1Id, uint256 parent2Id) external returns (uint256) {
        require(ownerOf(parent1Id) == msg.sender, "Not owner of parent1");
        require(ownerOf(parent2Id) == msg.sender, "Not owner of parent2");
        require(dragons[parent1Id].level >= 4, "Parent1 level too low");
        require(dragons[parent2Id].level >= 4, "Parent2 level too low");
        
        Dragon memory parent1 = dragons[parent1Id];
        Dragon memory parent2 = dragons[parent2Id];
        
        // Child inherits higher rarity
        Rarity childRarity = parent1.rarity > parent2.rarity ? parent1.rarity : parent2.rarity;
        
        // Random element/power from parents
        Element childElement = (uint256(keccak256(abi.encodePacked(block.timestamp, parent1Id))) % 2 == 0) 
            ? parent1.element : parent2.element;
        PowerType childPower = (uint256(keccak256(abi.encodePacked(block.timestamp, parent2Id))) % 2 == 0)
            ? parent1.powerType : parent2.powerType;
        
        // Average stats with rarity boost
        uint256 avgAttack = (parent1.attack + parent2.attack) / 2;
        uint256 avgDefense = (parent1.defense + parent2.defense) / 2;
        uint256 avgSpeed = (parent1.speed + parent2.speed) / 2;
        
        uint256 multiplier = rarityMultipliers[childRarity];
        
        uint256 dragonId = _tokenIdCounter++;
        
        dragons[dragonId] = Dragon({
            id: dragonId,
            element: childElement,
            powerType: childPower,
            rarity: childRarity,
            attack: (avgAttack * multiplier) / 10000,
            defense: (avgDefense * multiplier) / 10000,
            speed: (avgSpeed * multiplier) / 10000,
            level: 1,
            experience: 0,
            wins: 0,
            losses: 0,
            birthTime: block.timestamp,
            parentEggId: 0 // Bred, not hatched
        });
        
        userDragons[msg.sender].push(dragonId);
        _safeMint(msg.sender, dragonId);
        
        emit DragonHatched(dragonId, msg.sender, childElement, childRarity);
        
        return dragonId;
    }

    // ============ VIEW FUNCTIONS ============

    function getDragon(uint256 dragonId) external view returns (Dragon memory) {
        return dragons[dragonId];
    }

    function getUserDragons(address user) external view returns (uint256[] memory) {
        return userDragons[user];
    }

    function getBattlePower(uint256 dragonId) external view returns (uint256) {
        Dragon memory dragon = dragons[dragonId];
        return (dragon.attack + dragon.defense + dragon.speed) * dragon.level;
    }

    // ============ METADATA (OpenSea/Magic Eden Compatible) ============

    function tokenURI(uint256 tokenId) public view virtual override returns (string memory) {
        require(ownerOf(tokenId) != address(0), "Token doesn't exist");
        
        if (bytes(_baseTokenURI).length == 0) {
            return _buildDefaultMetadata(tokenId);
        }
        
        return string(abi.encodePacked(_baseTokenURI, tokenId.toString()));
    }

    function _buildDefaultMetadata(uint256 tokenId) private view returns (string memory) {
        Dragon memory dragon = dragons[tokenId];
        
        string memory elementStr = _elementToString(dragon.element);
        string memory rarityStr = _rarityToString(dragon.rarity);
        string memory powerStr = _powerTypeToString(dragon.powerType);
        
        return string(abi.encodePacked(
            'data:application/json;utf8,{"name":"',
            rarityStr,
            ' ',
            elementStr,
            ' Dragon #',
            tokenId.toString(),
            '","description":"A powerful dragon from Dragon City. Element: ',
            elementStr,
            ', Rarity: ',
            rarityStr,
            '","attributes":[',
            '{"trait_type":"Element","value":"', elementStr, '"},',
            '{"trait_type":"Rarity","value":"', rarityStr, '"},',
            '{"trait_type":"Power Type","value":"', powerStr, '"},',
            '{"trait_type":"Level","value":', dragon.level.toString(), '},',
            '{"trait_type":"Attack","value":', dragon.attack.toString(), '},',
            '{"trait_type":"Defense","value":', dragon.defense.toString(), '},',
            '{"trait_type":"Speed","value":', dragon.speed.toString(), '},',
            '{"trait_type":"Wins","value":', dragon.wins.toString(), '},',
            '{"trait_type":"Losses","value":', dragon.losses.toString(), '}',
            '],"image":"ipfs://QmYourImageHash/',
            rarityStr,
            '_',
            elementStr,
            '.png"}'
        ));
    }

    function _elementToString(Element element) private pure returns (string memory) {
        if (element == Element.FIRE) return "Fire";
        if (element == Element.WATER) return "Water";
        if (element == Element.EARTH) return "Earth";
        if (element == Element.AIR) return "Air";
        if (element == Element.DARK) return "Dark";
        return "Light";
    }

    function _rarityToString(Rarity rarity) private pure returns (string memory) {
        if (rarity == Rarity.LEGENDARY) return "Legendary";
        if (rarity == Rarity.UNIQUE) return "Unique";
        if (rarity == Rarity.RARE) return "Rare";
        return "Common";
    }

    function _powerTypeToString(PowerType powerType) private pure returns (string memory) {
        if (powerType == PowerType.PHYSICAL) return "Physical";
        if (powerType == PowerType.MAGICAL) return "Magical";
        return "Hybrid";
    }
}

contracts/EggNFTBatch.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC721/ERC721.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/Strings.sol";

/**
 * @title EggNFTBatch
 * @dev Batch mint 1000 eggs with rarity tiers for Dragon City game
 * Supports installment payments (3 payments over 72 hours)
 * OpenSea/Magic Eden compatible metadata
 */
contract EggNFTBatch is ERC721, Ownable {
    using Strings for uint256;

    // Rarity tiers
    enum Rarity { COMMON, RARE, UNIQUE, LEGENDARY }
    
    struct Egg {
        Rarity rarity;
        uint256 batchId;
        bool isHatched;
        uint256 mintedAt;
    }

    struct InstallmentPlan {
        address buyer;
        uint256 eggId;
        uint256 totalPrice;
        uint256 paidAmount;
        uint256 installmentsPaid; // 0, 1, 2, or 3 (complete)
        uint256 lastPaymentTime;
        bool isComplete;
        bool isActive;
    }

    // Constants
    uint256 public constant MAX_SUPPLY = 1000;
    uint256 public constant LEGENDARY_COUNT = 25;
    uint256 public constant UNIQUE_COUNT = 35;
    uint256 public constant RARE_COUNT = 45;
    uint256 public constant COMMON_COUNT = 895;
    
    uint256 public constant INSTALLMENT_COUNT = 3;
    uint256 public constant PAYMENT_INTERVAL = 24 hours;
    
    // Pricing (in wei)
    uint256 public constant LEGENDARY_PRICE = 0.5 ether;
    uint256 public constant UNIQUE_PRICE = 0.3 ether;
    uint256 public constant RARE_PRICE = 0.15 ether;
    uint256 public constant COMMON_PRICE = 0.05 ether;

    // State
    uint256 private _tokenIdCounter;
    uint256 public legendaryMinted;
    uint256 public uniqueMinted;
    uint256 public rareMinted;
    uint256 public commonMinted;
    
    bool public isFirstSaleActive = true; // Priority sale for Rare+ eggs
    uint256 public firstSaleEndTime;
    
    string private _baseTokenURI;
    address public dragonNFTContract;

    // Mappings
    mapping(uint256 => Egg) public eggs;
    mapping(uint256 => InstallmentPlan) public installmentPlans;
    mapping(address => uint256[]) public userInstallments;
    
    // Events
    event EggMinted(address indexed buyer, uint256 indexed tokenId, Rarity rarity, bool isInstallment);
    event InstallmentPayment(address indexed buyer, uint256 indexed eggId, uint256 payment, uint256 installmentNumber);
    event InstallmentCompleted(address indexed buyer, uint256 indexed eggId);
    event EggHatched(uint256 indexed tokenId, address indexed owner);
    event FirstSaleEnded();

    constructor() ERC721("Dragon Egg", "DREGG") Ownable(msg.sender) {
        _tokenIdCounter = 1;
        firstSaleEndTime = block.timestamp + 7 days; // 1 week first sale
    }

    // ============ ADMIN FUNCTIONS ============

    function setBaseURI(string memory baseURI) external onlyOwner {
        _baseTokenURI = baseURI;
    }

    function setDragonContract(address _dragonContract) external onlyOwner {
        dragonNFTContract = _dragonContract;
    }

    function endFirstSale() external onlyOwner {
        isFirstSaleActive = false;
        emit FirstSaleEnded();
    }

    // ============ MINTING FUNCTIONS ============

    /**
     * @dev Direct purchase - full payment
     */
    function mintEgg(Rarity _rarity) external payable {
        require(_tokenIdCounter <= MAX_SUPPLY, "All eggs minted");
        require(msg.value == getPriceForRarity(_rarity), "Incorrect payment");
        
        if (isFirstSaleActive) {
            require(_rarity != Rarity.COMMON, "First sale: Rare+ only");
        }
        
        _mintEggInternal(msg.sender, _rarity, false);
    }

    /**
     * @dev Start installment plan - first payment
     */
    function mintEggWithInstallment(Rarity _rarity) external payable {
        require(_tokenIdCounter <= MAX_SUPPLY, "All eggs minted");
        
        uint256 totalPrice = getPriceForRarity(_rarity);
        uint256 installmentAmount = totalPrice / INSTALLMENT_COUNT;
        
        require(msg.value == installmentAmount, "Incorrect first payment");
        
        if (isFirstSaleActive) {
            require(_rarity != Rarity.COMMON, "First sale: Rare+ only");
        }
        
        uint256 tokenId = _mintEggInternal(msg.sender, _rarity, true);
        
        // Create installment plan
        installmentPlans[tokenId] = InstallmentPlan({
            buyer: msg.sender,
            eggId: tokenId,
            totalPrice: totalPrice,
            paidAmount: installmentAmount,
            installmentsPaid: 1,
            lastPaymentTime: block.timestamp,
            isComplete: false,
            isActive: true
        });
        
        userInstallments[msg.sender].push(tokenId);
        
        emit InstallmentPayment(msg.sender, tokenId, installmentAmount, 1);
    }

    /**
     * @dev Make installment payment
     */
    function payInstallment(uint256 tokenId) external payable {
        InstallmentPlan storage plan = installmentPlans[tokenId];
        
        require(plan.isActive, "No active plan");
        require(plan.buyer == msg.sender, "Not your plan");
        require(!plan.isComplete, "Already complete");
        require(plan.installmentsPaid < INSTALLMENT_COUNT, "All paid");
        require(
            block.timestamp >= plan.lastPaymentTime + PAYMENT_INTERVAL,
            "Too early for next payment"
        );
        
        uint256 installmentAmount = plan.totalPrice / INSTALLMENT_COUNT;
        require(msg.value == installmentAmount, "Incorrect payment");
        
        plan.paidAmount += installmentAmount;
        plan.installmentsPaid += 1;
        plan.lastPaymentTime = block.timestamp;
        
        emit InstallmentPayment(msg.sender, tokenId, installmentAmount, plan.installmentsPaid);
        
        if (plan.installmentsPaid == INSTALLMENT_COUNT) {
            plan.isComplete = true;
            emit InstallmentCompleted(msg.sender, tokenId);
        }
    }

    /**
     * @dev Batch mint for owner (airdrops, rewards)
     */
    function batchMint(address[] memory recipients, Rarity[] memory rarities) external onlyOwner {
        require(recipients.length == rarities.length, "Length mismatch");
        
        for (uint256 i = 0; i < recipients.length; i++) {
            require(_tokenIdCounter <= MAX_SUPPLY, "Max supply reached");
            _mintEggInternal(recipients[i], rarities[i], false);
        }
    }

    /**
     * @dev Gacha/Raffle - random common egg
     */
    function mintGachaEgg() external payable {
        require(!isFirstSaleActive, "First sale active");
        require(msg.value == COMMON_PRICE, "Incorrect payment");
        require(commonMinted < COMMON_COUNT, "No common eggs left");
        
        _mintEggInternal(msg.sender, Rarity.COMMON, false);
    }

    // ============ HATCHING ============

    function hatchEgg(uint256 tokenId) external {
        require(ownerOf(tokenId) == msg.sender, "Not egg owner");
        require(!eggs[tokenId].isHatched, "Already hatched");
        
        // Check installment is complete if applicable
        InstallmentPlan memory plan = installmentPlans[tokenId];
        if (plan.isActive) {
            require(plan.isComplete, "Complete payments first");
        }
        
        eggs[tokenId].isHatched = true;
        
        emit EggHatched(tokenId, msg.sender);
    }

    // ============ INTERNAL ============

    function _mintEggInternal(address to, Rarity rarity, bool isInstallment) private returns (uint256) {
        require(_canMintRarity(rarity), "Rarity limit reached");
        
        uint256 tokenId = _tokenIdCounter;
        _tokenIdCounter++;
        
        _incrementRarityCount(rarity);
        
        eggs[tokenId] = Egg({
            rarity: rarity,
            batchId: 1, // First batch
            isHatched: false,
            mintedAt: block.timestamp
        });
        
        _safeMint(to, tokenId);
        
        emit EggMinted(to, tokenId, rarity, isInstallment);
        
        return tokenId;
    }

    function _canMintRarity(Rarity rarity) private view returns (bool) {
        if (rarity == Rarity.LEGENDARY) return legendaryMinted < LEGENDARY_COUNT;
        if (rarity == Rarity.UNIQUE) return uniqueMinted < UNIQUE_COUNT;
        if (rarity == Rarity.RARE) return rareMinted < RARE_COUNT;
        return commonMinted < COMMON_COUNT;
    }

    function _incrementRarityCount(Rarity rarity) private {
        if (rarity == Rarity.LEGENDARY) legendaryMinted++;
        else if (rarity == Rarity.UNIQUE) uniqueMinted++;
        else if (rarity == Rarity.RARE) rareMinted++;
        else commonMinted++;
    }

    // ============ VIEW FUNCTIONS ============

    function getPriceForRarity(Rarity rarity) public pure returns (uint256) {
        if (rarity == Rarity.LEGENDARY) return LEGENDARY_PRICE;
        if (rarity == Rarity.UNIQUE) return UNIQUE_PRICE;
        if (rarity == Rarity.RARE) return RARE_PRICE;
        return COMMON_PRICE;
    }

    function getInstallmentAmount(Rarity rarity) public pure returns (uint256) {
        return getPriceForRarity(rarity) / INSTALLMENT_COUNT;
    }

    function getEgg(uint256 tokenId) external view returns (Egg memory) {
        return eggs[tokenId];
    }

    function getUserInstallments(address user) external view returns (uint256[] memory) {
        return userInstallments[user];
    }

    function getInstallmentStatus(uint256 tokenId) external view returns (
        uint256 paidAmount,
        uint256 remainingAmount,
        uint256 nextPaymentDue,
        bool isComplete
    ) {
        InstallmentPlan memory plan = installmentPlans[tokenId];
        
        paidAmount = plan.paidAmount;
        remainingAmount = plan.totalPrice - plan.paidAmount;
        nextPaymentDue = plan.lastPaymentTime + PAYMENT_INTERVAL;
        isComplete = plan.isComplete;
    }

    function getRaritySupply() external view returns (
        uint256 legendary,
        uint256 unique,
        uint256 rare,
        uint256 common
    ) {
        legendary = legendaryMinted;
        unique = uniqueMinted;
        rare = rareMinted;
        common = commonMinted;
    }

    // ============ METADATA (OpenSea Compatible) ============

    function tokenURI(uint256 tokenId) public view virtual override returns (string memory) {
        require(ownerOf(tokenId) != address(0), "Token doesn't exist");
        
        if (bytes(_baseTokenURI).length == 0) {
            return _buildDefaultMetadata(tokenId);
        }
        
        return string(abi.encodePacked(_baseTokenURI, tokenId.toString()));
    }

    function _buildDefaultMetadata(uint256 tokenId) private view returns (string memory) {
        Egg memory egg = eggs[tokenId];
        string memory rarityStr = _rarityToString(egg.rarity);
        
        // OpenSea-compatible JSON
        return string(abi.encodePacked(
            'data:application/json;utf8,{"name":"Dragon Egg #',
            tokenId.toString(),
            '","description":"A mystical dragon egg from Dragon City. Rarity: ',
            rarityStr,
            '","attributes":[{"trait_type":"Rarity","value":"',
            rarityStr,
            '"},{"trait_type":"Status","value":"',
            egg.isHatched ? "Hatched" : "Unhatched",
            '"},{"trait_type":"Batch","value":',
            egg.batchId.toString(),
            '}],"image":"ipfs://QmYourImageHash/',
            rarityStr,
            '.png"}'
        ));
    }

    function _rarityToString(Rarity rarity) private pure returns (string memory) {
        if (rarity == Rarity.LEGENDARY) return "Legendary";
        if (rarity == Rarity.UNIQUE) return "Unique";
        if (rarity == Rarity.RARE) return "Rare";
        return "Common";
    }

    // ============ WITHDRAWAL ============

    function withdraw() external onlyOwner {
        uint256 balance = address(this).balance;
        require(balance > 0, "No funds");
        payable(owner()).transfer(balance);
    }
}

contracts/Leaderboard.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";

/**
 * @title Leaderboard
 * @dev Track player points and distribute rewards
 * Points earned from: battles, breeding, hatching
 */
contract Leaderboard is Ownable {
    struct PlayerStats {
        uint256 totalPoints;
        uint256 battlesWon;
        uint256 dragonsHatched;
        uint256 dragonsBred;
        uint256 rewardsClaimed;
        uint256 lastActivityTime;
        uint256 rank;
    }

    struct RewardTier {
        uint256 minPoints;
        uint256 rewardAmount;
        string tierName;
    }

    // Points configuration
    uint256 public constant POINTS_PER_WIN = 100;
    uint256 public constant POINTS_PER_HATCH = 50;
    uint256 public constant POINTS_PER_BREED = 150;
    uint256 public constant POINTS_PER_RARE_HATCH = 200;
    uint256 public constant POINTS_PER_LEGENDARY_HATCH = 500;

    // State
    mapping(address => PlayerStats) public playerStats;
    address[] public players;
    mapping(address => bool) public isPlayer;
    
    RewardTier[] public rewardTiers;
    mapping(address => uint256) public claimableRewards;
    
    address public dragonNFTContract;
    address public rewardToken; // Optional ERC20 reward token
    
    bool public rewardsEnabled;
    uint256 public totalPointsDistributed;
    
    // Events
    event PointsAwarded(address indexed player, uint256 points, string reason);
    event RewardClaimed(address indexed player, uint256 amount);
    event RankUpdated(address indexed player, uint256 newRank);
    event NewLeader(address indexed player, uint256 totalPoints);

    constructor() Ownable(msg.sender) {
        // Initialize default reward tiers
        rewardTiers.push(RewardTier(1000, 0.01 ether, "Bronze"));
        rewardTiers.push(RewardTier(5000, 0.05 ether, "Silver"));
        rewardTiers.push(RewardTier(10000, 0.15 ether, "Gold"));
        rewardTiers.push(RewardTier(25000, 0.5 ether, "Platinum"));
        rewardTiers.push(RewardTier(50000, 1 ether, "Diamond"));
    }

    // ============ ADMIN FUNCTIONS ============

    function setDragonNFTContract(address _dragonNFT) external onlyOwner {
        dragonNFTContract = _dragonNFT;
    }

    function setRewardToken(address _token) external onlyOwner {
        rewardToken = _token;
    }

    function setRewardsEnabled(bool _enabled) external onlyOwner {
        rewardsEnabled = _enabled;
    }

    function addRewardTier(uint256 minPoints, uint256 rewardAmount, string memory tierName) external onlyOwner {
        rewardTiers.push(RewardTier(minPoints, rewardAmount, tierName));
    }

    function fundRewards() external payable onlyOwner {
        require(msg.value > 0, "No funds sent");
    }

    // ============ POINT TRACKING ============

    function recordBattleWin(address player) external {
        require(msg.sender == dragonNFTContract || msg.sender == owner(), "Not authorized");
        
        _addPlayer(player);
        
        playerStats[player].battlesWon++;
        playerStats[player].totalPoints += POINTS_PER_WIN;
        playerStats[player].lastActivityTime = block.timestamp;
        
        totalPointsDistributed += POINTS_PER_WIN;
        
        _updateRanking();
        _checkRewardEligibility(player);
        
        emit PointsAwarded(player, POINTS_PER_WIN, "Battle Win");
    }

    function recordHatch(address player, bool isRare, bool isLegendary) external {
        require(msg.sender == dragonNFTContract || msg.sender == owner(), "Not authorized");
        
        _addPlayer(player);
        
        uint256 points = POINTS_PER_HATCH;
        if (isLegendary) points = POINTS_PER_LEGENDARY_HATCH;
        else if (isRare) points = POINTS_PER_RARE_HATCH;
        
        playerStats[player].dragonsHatched++;
        playerStats[player].totalPoints += points;
        playerStats[player].lastActivityTime = block.timestamp;
        
        totalPointsDistributed += points;
        
        _updateRanking();
        _checkRewardEligibility(player);
        
        emit PointsAwarded(player, points, isLegendary ? "Legendary Hatch" : isRare ? "Rare Hatch" : "Hatch");
    }

    function recordBreed(address player) external {
        require(msg.sender == dragonNFTContract || msg.sender == owner(), "Not authorized");
        
        _addPlayer(player);
        
        playerStats[player].dragonsBred++;
        playerStats[player].totalPoints += POINTS_PER_BREED;
        playerStats[player].lastActivityTime = block.timestamp;
        
        totalPointsDistributed += POINTS_PER_BREED;
        
        _updateRanking();
        _checkRewardEligibility(player);
        
        emit PointsAwarded(player, POINTS_PER_BREED, "Breeding");
    }

    function _addPlayer(address player) private {
        if (!isPlayer[player]) {
            players.push(player);
            isPlayer[player] = true;
        }
    }

    function _updateRanking() private {
        // Simple bubble sort for top players (optimize for production)
        for (uint256 i = 0; i < players.length && i < 100; i++) {
            for (uint256 j = i + 1; j < players.length && j < 100; j++) {
                if (playerStats[players[j]].totalPoints > playerStats[players[i]].totalPoints) {
                    address temp = players[i];
                    players[i] = players[j];
                    players[j] = temp;
                }
            }
        }
        
        // Update ranks
        for (uint256 i = 0; i < players.length && i < 100; i++) {
            uint256 newRank = i + 1;
            if (playerStats[players[i]].rank != newRank) {
                playerStats[players[i]].rank = newRank;
                emit RankUpdated(players[i], newRank);
                
                if (newRank == 1) {
                    emit NewLeader(players[i], playerStats[players[i]].totalPoints);
                }
            }
        }
    }

    function _checkRewardEligibility(address player) private {
        if (!rewardsEnabled) return;
        
        uint256 points = playerStats[player].totalPoints;
        
        for (uint256 i = rewardTiers.length; i > 0; i--) {
            RewardTier memory tier = rewardTiers[i - 1];
            if (points >= tier.minPoints) {
                uint256 potentialReward = tier.rewardAmount;
                if (potentialReward > claimableRewards[player]) {
                    claimableRewards[player] = potentialReward;
                }
                break;
            }
        }
    }

    // ============ REWARD CLAIMING ============

    function claimReward() external {
        require(rewardsEnabled, "Rewards not enabled");
        
        uint256 reward = claimableRewards[msg.sender];
        require(reward > 0, "No rewards available");
        require(address(this).balance >= reward, "Insufficient contract balance");
        
        claimableRewards[msg.sender] = 0;
        playerStats[msg.sender].rewardsClaimed += reward;
        
        payable(msg.sender).transfer(reward);
        
        emit RewardClaimed(msg.sender, reward);
    }

    // ============ VIEW FUNCTIONS ============

    function getPlayerStats(address player) external view returns (PlayerStats memory) {
        return playerStats[player];
    }

    function getTopPlayers(uint256 count) external view returns (address[] memory, uint256[] memory) {
        uint256 length = count > players.length ? players.length : count;
        address[] memory topAddresses = new address[](length);
        uint256[] memory topPoints = new uint256[](length);
        
        for (uint256 i = 0; i < length; i++) {
            topAddresses[i] = players[i];
            topPoints[i] = playerStats[players[i]].totalPoints;
        }
        
        return (topAddresses, topPoints);
    }

    function getPlayerRank(address player) external view returns (uint256) {
        return playerStats[player].rank;
    }

    function getRewardTiers() external view returns (RewardTier[] memory) {
        return rewardTiers;
    }

    function getTotalPlayers() external view returns (uint256) {
        return players.length;
    }

    // ============ WITHDRAWAL ============

    function withdrawFunds() external onlyOwner {
        uint256 balance = address(this).balance;
        require(balance > 0, "No funds");
        payable(owner()).transfer(balance);
    }

    receive() external payable {}
}

